import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../client';
import './Postdetail.css';
import { Link } from 'react-router-dom';
import Card from '../components/Card.js';


const DetPosts = () => {
    const [selectedCard, setSelectedCard] = useState(null);
    const { id } = useParams();  // Retrieve the 'id' from the URL
    const [likes, setLikes] = useState(likes || 0);
    const [count, setCount] = useState(0);  // State to track the count of clicks

    const incrementLikes = async (postId) => {
      try {
        // Step 1: Fetch the current number of likes
        const { data: currentData, error: fetchError } = await supabase
          .from('Posts')
          .select('likes')
          .eq('id', postId)
          .single();
    
        if (fetchError) throw fetchError;
    
        // Step 2: Increment the likes count
        const newLikes = (currentData.likes || 0) + 1;
    
        // Step 3: Update the post with the new likes count
        const { data, error } = await supabase
          .from('Posts')
          .update({ likes: newLikes })
          .match({ id: postId });
    
        if (error) throw error;
    
        return newLikes;  // Return the updated likes count
      } catch (error) {
        console.error('Error updating likes:', error);
        return null;  // Return null in case of error
      }
    };
    
    const handleLike = async () => {
      const newLikes = await incrementLikes(id);
      if (newLikes !== null) {
        setLikes(newLikes);
        setCount(count + 1);  // Update local state if the database update was successful
      }
    };

    useEffect(() => {
        async function fetchPost() {
            // Fetch the post using the id from the URL parameter
            const { data, error } = await supabase
                .from('Posts')
                .select('*')
                .eq('id', id)
                .single();

            if (error) {
                console.error('Error fetching post:', error);
                return;
            }

            setSelectedCard(data);  // Set the retrieved post to state
        }

        fetchPost();
    }, [id]);  // Dependency on 'id' ensures the effect runs when 'id' changes

    // Conditional rendering to handle loading and error state
    if (!selectedCard) {
        return <div className="DetPosts"><h2>Loading or no data found...</h2></div>;
    }

    return (
        <div className="DetPosts">
            <div>
                <h1>{selectedCard.title}</h1>  
                <p>Meal Type:  {selectedCard.description}</p>
                <p>Recipe:  {selectedCard.author}</p>
                <Link to="/"><button className="headerBtn"> Back to Home</button></Link>
                <Link to={`/edit/${selectedCard.id}`}><button>Edit or Delete</button></Link>
                <button className="likeButton" onClick={handleLike}>
        Like {likes}  
      </button>

                
            </div>
        </div>
    );
};

export default DetPosts;
