import React from 'react'
import { useState } from 'react'
import './Card.css'
import more from './more.png'
import { Link } from 'react-router-dom'
import deletePost from '../pages/EditPost.js'


const Card = (props) =>  {
  console.log("Created at:", props.created_at);

  const [count, setCount] = useState(0)
  const updateCount = () => {
    setCount((count) => count + 1);
  }
  const date = new Date(props.created_at).toLocaleDateString("en-US", {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit'
  });


  return (
      <div className="Card">
         <p className="timestamp">Created: {date}</p>  
          <Link to={'edit/'+ props.id}><img className="moreButton" alt="edit button" src={more} /></Link>
          <h2 className="title">{props.title}</h2>
          <p className="description">{"Meal Type: " + props.description}</p>
          <Link to={`/posts/${props.id}`}><button className="headerBtn"> Recipe </button></Link>

      </div>
  );
};

export default Card;
