import React, { useState, useEffect } from 'react';
import Card from '../components/Card';
import { supabase } from '../client';


const DetPosts = (props) => {
    const [posts, setPosts] = useState([]);
    const [selectedCard, setSelectedCard] = useState(null);

    useEffect(() => {
        async function fetchPosts() {
            const { data } = await supabase.from('Posts').select();
            setPosts(data);
        }
        fetchPosts();
    }, []);

    const handleCardSelect = (card) => {
        setSelectedCard(card);
    };

    return (
        <div className="ReadPosts">
            {selectedCard ? (
                <div>
                    <h1>{selectedCard.title}</h1>
                    <img src={`${process.env.PUBLIC_URL}/whitecrew.png`} alt="sus" style={{ maxWidth: '500px', height: 'auto' }} />
                    <p>Speed: {selectedCard.author}</p>
                    <p>Color: {selectedCard.description}</p>
                    <button onClick={() => setSelectedCard(null)}>Back to list</button>
                </div>
            ) : (
                <div>
                    {posts.length > 0 ? (
                        posts.map(post => (
                            <div key={post.id} onClick={() => handleCardSelect(post)}>
                                <Card {...post} />
                            </div>
                        ))
                    ) : (
                        <h2>Empty Crew huh?</h2>
                    )}
                </div>
            )}
        </div>
    );
};

export default DetPosts;
