import './Card.css'
import more from './more.png'
import { Link } from 'react-router-dom'
import deletePost from '../pages/EditPost.js'
import React, { useState, useEffect } from 'react';
import { supabase } from '../client';


const Card = (props) =>  {
  

  const [count, setCount] = useState(0)
  const updateCount = () => {
    setCount((count) => count + 1);
  };
  // Example of fetching data including the created_at field
useEffect(() => {
  async function fetchPosts() {
      const { data, error } = await supabase
          .from('Posts')
          .select('*');  // Make sure 'created_at' is part of your table schema and being fetched here
      if (error) {
          console.error('Error fetching posts:', error);
      } else {
          setPosts(data);
      }
  }
  fetchPosts();
}, []);



  return (
      <div className="Card">
         <p className="timestamp">Created: {props.created_at}</p>
          <Link to={'edit/'+ props.id}><img className="moreButton" alt="edit button" src={more} /></Link>
          <h2 className="title">{props.title}</h2>
          <p className="description">{"Meal Type: " + props.description}</p>
          <Link to={`/posts/${props.id}`}><button className="headerBtn"> Recipe </button></Link>

      </div>
  );
};

export default Card;
