    setCount((count) => count + 1);
  }
  const handleCardSelect = (card) => {
    setSelectedCard(card);
  };
  

  return (
      <div className="Card">
          <Link to={'edit/'+ props.id}><img className="moreButton" alt="edit button" src={more} /></Link>
          <h2 className="title">{props.title}</h2>
          <img src={`${process.env.PUBLIC_URL}/shadowpika.png`} alt="sus" style={{ maxWidth: '100px', height: 'auto' }} />
          <h3 className="author">{"Speed: " + props.author}</h3>
          <p className="description">{"Color:" + props.description}</p>
          <Link to='/dets/:id'><button className="headerBtn"> Info </button></Link>
          <Link to={`/posts/${props.id}`}>View Details</Link>
      </div>
  );
};

export default Card;
