import React, { useState, useEffect } from 'react';
import Card from '../components/Card';
import { supabase } from '../client';


const DetPosts = (props) => {
    const [selectedCard, setSelectedCard] = useState(null);


    const [posts, setPosts] = useState([]);

    useEffect(() => {
        const fetchPosts = async () => {
            const {data} = await supabase
              .from('Posts')
              .select();
          
            // set state of posts
            setPosts(data)
          }
        setPosts(props.data);
        fetchPosts();
    }, [props]);
    
    return (
        <div className="ReadPosts">
            <img src={`${process.env.PUBLIC_URL}/whitecrew.png`} alt="sus" style={{ maxWidth: '200px', height: 'auto' }} />
            {
                posts && posts.length > 0 ?
                posts.map((post,index) => 
                   <Card id={post.id} title={post.title} author={post.author} description={post.description}/>
                ) : <h2>{'Empty Crew huh?'}</h2>
            }
           
        </div>  
    )
}

export default DetPosts;
