import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../client';
import './Postdetail.css';
import { Link } from 'react-router-dom';
import Card from 'client/src/components/Card.js'


const DetPosts = () => {
    const [selectedCard, setSelectedCard] = useState(null);
    const { id } = useParams();  // Retrieve the 'id' from the URL

    useEffect(() => {
        async function fetchPost() {
            // Fetch the post using the id from the URL parameter
            const { data, error } = await supabase
                .from('Posts')
                .select('*')
                .eq('id', id)
                .single();

            if (error) {
                console.error('Error fetching post:', error);
                return;
            }

            setSelectedCard(data);  // Set the retrieved post to state
        }

        fetchPost();
    }, [id]);  // Dependency on 'id' ensures the effect runs when 'id' changes

    // Conditional rendering to handle loading and error state
    if (!selectedCard) {
        return <div className="DetPosts"><h2>Loading or no data found...</h2></div>;
    }

    return (
        <div className="DetPosts">
            <div>
                <h1>{selectedCard.title}</h1> 
                <img src={`${process.env.PUBLIC_URL}/whitecrew.png`} alt="Detailed view" style={{ maxWidth: '500px', height: 'auto' }} />
                <p>Speed: {selectedCard.author}</p>
                <p>Color: {selectedCard.description}</p>
                <Link to="/"><button className="headerBtn"> Back to Crew</button></Link>
                
            </div>
        </div>
    );
};

export default DetPosts;
