import { supabase } from '../client';
import './Card.css';
import more from './more.png';
import { Link } from 'react-router-dom';
import React, { useState } from 'react';

const Card = (props) => {
  // Displaying the creation date. Assuming `created_at` is in ISO 8601 format and passed as a prop.
  const [likes, setLikes] = useState(props.likes || 0); // Initialize likes from props

    const handleLike = async () => {
        const updatedLikes = await incrementLikes(props.id); // Increment likes in the database
        if (updatedLikes) {
            setLikes(likes + 1); // Update local state if the database update was successful
        }
    };
  const formatDate = (dateString) => {
    if (!dateString) return 'No date provided';
    const date = new Date(dateString);
    return date.toLocaleDateString("en-US", {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  }
  const [count, setCount] = useState(0);  // State to track the count of clicks

  const incrementLikes = async (postId) => {
    const { data, error } = await supabase
        .from('Posts')
        .update({ likes: supabase.raw('likes + 1') }) // Use raw SQL to increment likes
        .match({ id: postId });

    if (error) {
        console.error('Error updating likes:', error);
    }
    return data;
};

  const incrementCount = () => {
    setCount(count + 1);  // Increment the count state by 1 on each click
  };;

  return (
    <div className="Card">
      <Link to={'edit/' + props.id}>
        <img className="moreButton" alt="edit button" src={more} />
      </Link>
      <p className="timestamp">Created: {formatDate(props.created_at)}</p>
      <h2 className="title">{props.title}</h2>
      <p className="description">{"Meal Type: " + props.description}</p>
      <Link to={`/posts/${props.id}`}>
        <button className="headerBtn">Recipe</button>
      </Link>
      <button className="likeButton" onClick={handleLike}>
        Like {likes}  
      </button>
    </div>
  );
};

export default Card;
