import './Card.css'
import more from './more.png'
import { Link } from 'react-router-dom'
import deletePost from '../pages/EditPost.js'
import React, { useState, useEffect } from 'react';
import { supabase } from '../client';


const Card = (props) =>  {
  console.log("Created at:", props.created_at);

  const [count, setCount] = useState(0)
  const updateCount = () => {
    setCount((count) => count + 1);
  };
  useEffect(() => {
    async function fetchPost() {
        // Fetch the post using the id from the URL parameter
        const { data, error } = await supabase
            .from('Posts')
            .select('*')
            .eq('id', id)
            .single();

        if (error) {
            console.error('Error fetching post:', error);
            return;
        }

        setSelectedCard(data);  // Set the retrieved post to state
    }

    fetchPost();
}, [id]);
 



  return (
      <div className="Card">
         <p className="timestamp">Created: {props.created_at}</p>
          <Link to={'edit/'+ props.id}><img className="moreButton" alt="edit button" src={more} /></Link>
          <h2 className="title">{props.title}</h2>
          <p className="description">{"Meal Type: " + props.description}</p>
          <Link to={`/posts/${props.id}`}><button className="headerBtn"> Recipe </button></Link>

      </div>
  );
};

export default Card;
